variables.value:
.
| (split("\n")
   | map(rtrimstr("\r"))                     # handle CRLF just in case
   | map(sub("^\\s*"; ""))                   # trim ANY leading whitespace
   | map(select(. != ""))                    # drop blank lines
   | map(select(. != "metadata_details:"))   # drop the top-level header
   | map(capture("^(?<k>[A-Za-z0-9_\\-]+):\\s*(?<v>.*)$")?)
   | map(select(. != null))
   | reduce .[] as $kv ({};
       ($kv.v | gsub("^\"|\"$"; "")) as $v   # strip surrounding quotes if present
       | . + {
           ($kv.k):
             (if $kv.k == "tags"
              then ($v | split(",") | map(gsub("^\\s+|\\s+$"; "")))
              else $v
              end)
         }
     )
 )
| { metadata_details: . }

def parse_metadata:
  (split("\n")
   | map(rtrimstr("\r"))
   | map(sub("^\\s*"; ""))                 # trim any leading spaces
   | map(select(length > 0))
   | map(select(. != "metadata_details:")) # drop the header line
   | map(capture("^(?<k>[A-Za-z0-9_\\-]+):\\s*(?<v>.*)$")?)
   | map(select(. != null))
   | reduce .[] as $kv ({};
       ($kv.v | gsub("^\"|\"$"; "")) as $v  # strip surrounding quotes
       | . + {
           ($kv.k):
             (if $kv.k == "tags"
              then ($v | split(",") | map(gsub("^\\s+|\\s+$"; "")) | map(select(length>0)))
              else $v
              end)
         }
     )
  );

(.body? // .) as $s
| ($s | parse_metadata) as $md
| if (type == "object" and (.body? // null) != null) then
    . + { metadata_details: $md }           # merge onto original object
  else
    { metadata_details: $md }               # input was a string; just return the parsed object
  end

